import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";

function App() {
  const dispatch = useDispatch();
  const cartState = useSelector((state) => state.user.isVisible);
  const cart = useSelector((state) => state.cart.cartItems);

  useEffect(() => {
    const updatingCart = async () => {
      dispatch({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data...",
      });

      const response = await fetch(
        "https://website-development-64082-default-rtdb.firebaseio.com/cart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );

      if (!response.ok) {
        throw new Error();
      }

      dispatch({
        status: "success",
        title: "Cart Data Fetched",
        message: "Cart data fetched successfully",
      });
    };

    updatingCart().catch((err) => {
      dispatch({
        status: "error",
        title: "Error in fetching cart",
        message: "Sending cart data failed",
      });
    });
  }, [cart, dispatch]);

  return <Layout>{cartState ? <Cart /> : <Products />}</Layout>;
}

export default App;
