import { createSlice } from "@reduxjs/toolkit";

const userInterfaceSlice = createSlice({
  name: "interface",
  initialState: { isVisible: false, notification: null },
  reducers: {
    toggleCart(state) {
      state.isVisible = !state.isVisible;
    },

    showNotification(state, action) {
      state.notification = {
        status: action.payload.status,
        title: action.payload.title,
        message: action.payload.message,
      };
    },
  },
});

export const sendCartData = (cart) => {
  return async (dispatch) => {
    dispatch(
      userActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data...",
      })
    );

    const fetchingData = async () => {
      await fetch(
        "https://website-development-64082-default-rtdb.firebaseio.com/cart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );
    };

    try {
      await fetchingData();
      dispatch(
        userActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Cart data fetched successfully",
        })
      );
    } catch (error) {
      dispatch(
        userActions.showNotification({
          status: "error",
          title: "Error ðŸ”´",
          message: "Sending cart data failed",
        })
      );
    }
  };
};

export const userActions = userInterfaceSlice.actions;

export default userInterfaceSlice;
