import { useDispatch, useSelector } from "react-redux";
import { useEffect, Fragment } from "react";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { userActions } from "./store/index";
import Notification from "./components/UI/Notification";

let isInitial;

function App() {
  const dispatch = useDispatch();
  const cartState = useSelector((state) => state.user.isVisible);
  const cart = useSelector((state) => state.cart.cartItems);
  const notification = useSelector((state) => state.user.notification);

  useEffect(() => {
    const updatingCart = async () => {
      dispatch(
        userActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending cart data...",
        })
      );

      const response = await fetch(
        "https://website-development-64082-default-rtdb.firebaseio.com/cart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );

      if (!response.ok) {
        throw new Error();
      }

      dispatch(
        userActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Cart data fetched successfully",
        })
      );
    };

    if (isInitial) {
      isInitial = false;
      return;
    }

    updatingCart().catch((err) => {
      dispatch(
        userActions.showNotification({
          status: "error",
          title: "Error ðŸ”´",
          message: "Sending cart data failed",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>{cartState ? <Cart /> : <Products />}</Layout>
    </Fragment>
  );
}

export default App;
